import CardsContainer from "../../components/CardsContainer/CardsContainer";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs, getByName } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar";
import FilterBy from "../../components/FilterBy/FilterBy";

function Home(props) {
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.allDogs);
  const [searchString, setSearchString] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const  [filtered, setFiltered] = useState(allDogs);


  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  const handleChange = (e) => {    //funcion que setea 
        setSearchString(e.target.value);
        const filteredDogs = allDogs.filter((dog) => {
          return dog.name.toLowerCase().includes(e.target.value.toLowerCase());
        });
        setFiltered(filteredDogs);
      };

  const totalPages = Math.ceil(allDogs.length / 8);

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handlePrevPage = () => {
    setCurrentPage(currentPage - 1);
  };



  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(getByName(searchString));
  };

  return (
    <>
      <button onClick={handlePrevPage} disabled={currentPage === 1}>
        Anterior
      </button>
      <span>PÃ¡gina {currentPage}</span>
      <button
        onClick={handleNextPage}
        disabled={currentPage === totalPages}
      >
        Siguiente
      </button>

      <SearchBar handleChange={handleChange} handleSubmit={handleSubmit} />
      <FilterBy />

      <CardsContainer currentPage={currentPage} cards={allDogs} />

      <h1>Estoy en Home</h1>
    </>
  );
}

export default Home;
