import style from "./FilterBy.module.css"; 
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByTemperaments } from "../../redux/actions";

const FilterBy = () => {
  const dispatch = useDispatch();
  const temperaments = useSelector((state) => state.temperaments);
  const [showTemperaments, setShowTemperaments] = useState(false);

  useEffect(() => {
    dispatch(getByTemperaments());
  }, [dispatch]);

  return (
    <div>
        <div className={style.temperament}>
      <button className={style.select}onClick={() => setShowTemperaments(!showTemperaments)}>Show Temperaments</button>
      {showTemperaments && (
        <div>

          <ol>
            {temperaments.map((temperaments) => (
              <li key={temperaments.id}>{temperament.name}</li>
            ))}
          </ol>
        </div>
      )}
    </div>
    </div>
  );
};

export default FilterBy;

