// export default Home 
import CardsContainer from "../../components/CardsContainer/CardsContainer";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs, getDogsByPage } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar";
import FilterBy from "../../components/FilterBy/FilterBy";
import Pagination from "../../components/Pagination/Pagination";

function Home() {
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.allDogs);
  const [filtered, setFiltered] = useState(allDogs);
  const [searchString, setSearchString] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(8);

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  useEffect(() => {
    setFiltered(allDogs);
  }, [allDogs]);

  useEffect(() => {
    dispatch(getDogsByPage(currentPage, itemsPerPage));
  }, [dispatch, currentPage, itemsPerPage]);

  const handleChange = (e) => {
    setSearchString(e.target.value);
    const filteredDogs = allDogs.filter((dog) => {
      return dog.name.toLowerCase().includes(e.target.value.toLowerCase());
    });
    setFiltered(filteredDogs);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(getDogsByPage(1, itemsPerPage, searchString));
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(filtered.length / itemsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <>
      <SearchBar handleChange={handleChange} handleSubmit={handleSubmit} />
      <FilterBy />

      <CardsContainer allDogs={filtered} />

      <Pagination
        itemsPerPage={itemsPerPage}
        totalItems={filtered.length}
        paginate={paginate}
        currentPage={currentPage}
        pageNumbers={pageNumbers}
      />

      <h1>Estoy en Home</h1>
    </>
  );
}

export default Home;
