import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByDetail } from "../../redux/actions";
import style from "./Detail.module.css";
import { useParams } from "react-router-dom";

function Detail() {
    const dispatch = useDispatch();


    const { idRaza } = useParams(); // Obtenemos el valor del parámetro "id"

    useEffect(() => {
      dispatch(getByDetail(idRaza)); // Pasamos el id al action creator
    }, [dispatch, idRaza]);

    const detailDog = useSelector((state) => state.detailDog);

    if (!detailDog) {
    return <h1>Loading...</h1>;
  }



    return (
        <div className={style.container}>
          <h2>{detail.name}</h2>            
            <img src={detail.image} alt={detail.name}/>
            {detail.temperaments?<p> <b>Temperaments: </b>{detail.temperaments}.</p>: null}
            {detail.breed_group?<p> <b>Groups: </b>{detail.breed_group}.</p>: null}
            <p><b>Height: </b>{detail.height} metric</p>
            <p><b>Weight: </b>{detail.weight} KG.</p>
            <p><b>Life Span: </b>{detail.life_span}</p>

        </div>
      );
    }

    export default Detail;
