import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import validate from "./validate";
import style  from "./Form.module.css"
import { getTemperaments } from "../../redux/actions";
import { useEffect } from "react";
import {  useDispatch, useSelector } from "react-redux"; //useSelector
const Form = () => {
  //formulario y estado deben de tener los mismos valores
  const dispatch = useDispatch(); // Aquí guardamos el hook useDispatch en una constante
  const temperaments = useSelector((state) => state.temperaments);


  const [form, setForm] = useState({
    name: "",
    min_height: "",
    max_height: "",
    min_weight: "",
    max_weight: "",
    life_span: "",
    temperaments: [],
    image: "",
  });

  const [errors, setErrors] = useState({
    name: "",
    min_height: "",
    max_height: "",
    min_weight: "",
    max_weight: "",
    weight: "",
    life_span: "",
    temperaments: [],
    image: "",
  });
  useEffect(() => {
    // Utilizamos el dispatch para llamar a la función getTemperaments
    dispatch(getTemperaments());
  }, [dispatch]);


  const changeHandler = (e) => {
    //e, me dice quien disparo el event
    const property = e.target.name;
    const value = e.target.value;

    setErrors(validate({ ...form, [property]: value })); //el mismo estado tiene validate y va a la par
    setForm({ ...form, [property]: value }); //cambio el estado y cambia el input
  };

  const submitHandler = (e) => {
    const height = `${form.min_height}-${form.max_height}`; //creo las variables y las concateno separandolas con un guion
    const weight = `${form.min_weight}-${form.max_weight}`;

    e.preventDefault(); ///ya no se va la pag ref
    axios
      .post("http://localhost:3001/dogs", { ...form, height, weight }) //envio el body form  junto con las variables (weight y hei)
      .then((res) => alert(res))
      .catch((err) => alert(err));
  };
  const handleTemperaments = (e) => {
    if (!temperaments.includes(temperaments.find( item => item.name === e.target.value))) {
        completed.temperaments.push(temperaments.find( item => item.name === e.target.value))
       }
    // completed.temperaments.push(tempForm.find( item => item.name === e.target.value))
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,
      })
    );
  };


  return (
    <div>
        <h2 className={style.title}>Create a new Dog</h2>


    <div className= {style.cardContainer}>


    <form className= { style.ent} onSubmit={submitHandler}>
      <div>


        <label className={style.label}>Name:</label>
        <input
          type="text "
          value={form.name}
          onChange={changeHandler}
          name="name"
          placeholder="Name"

        />
        {errors.name && <span>{errors.name}</span>}
      </div>

      <div>
        <label className={style.label} >Min Height:</label>
        <input
          type="text"
          value={form.min_height}
          onChange={changeHandler}
          name="min_height"

          className="number"
              placeholder="Height Min"

        />
        {errors.min_height && <span>{errors.min_height}</span>}
      </div>

      <div>
        <label className={style.label} >Max Height:</label>
        <input
          type="text"
          value={form.max_height}
          onChange={changeHandler}
          name="max_height"
          placeholder="Height Max"
        />
        {errors.max_height && <span>{errors.max_height}</span>}
      </div>
      <div></div>
      <div>
        <label className={style.label}>Min Weight:</label>
        <input
          type="text"
          value={form.min_weight}
          onChange={changeHandler}
          name="min_weight"
          placeholder="Weight Min"
        />
         {errors.min_weight && <span>{errors.min_weight}</span>}
      </div>
      <div>
        <label className={style.label}>Max Weight:</label>
        <input
          type="text"
          value={form.max_weight}
          onChange={changeHandler}
          name="max_weight"
          placeholder="Weight Max"
        />
        {errors.max_weight && <span>{errors.max_weight}</span>}
      </div>
      <div>
        <label className={style.label}>Life Span:</label>
        <input
          type="text "
          value={form.life_span}
          onChange={changeHandler}
          name="life_span"
          placeholder="Life Span"
        />
        {errors.life_span && <span>{errors.life_span}</span>}
      </div>
      <div>
        <p>Temperaments:</p>
        <select name="temperaments" onChange={handleTemperaments}>
        <option value="default">Choose</option>

{temperaments?.map((item) => (
                <option value={item.name} key={item.id}>
                  {item.name}
                </option>
              ))}
  </select> {errors.temperaments ? <label>{errors.temperaments}</label> : null}  
      </div>
      <div>
        <label className={style.label}>image:</label>
        <input
          type="text "
          value={form.image}
          onChange={changeHandler}
          name="image"
          placeholder="Put a URL"
        />
      </div>
      <button type="submit">Lets Create</button>
    </form>

    </div>
    <Link to="/home">
    <button className={style.btn}>Back To Home</button>
    </Link>
    </div>
  );
};

export default Form;
