import style from "./FilterBy.module.css";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTemperaments } from "../../redux/actions";

const FilterBy = ({ onChange }) => {
  const dispatch = useDispatch();
  const allTemperaments = useSelector((state) => state.temperaments);
  const [selectedTemperament, setSelectedTemperament] = useState("");

  useEffect(() => {
    dispatch(getTemperaments());
  }, [dispatch]);

  const handleTemperamentSelect = (event) => {
    const selectedValue = event.target.value;
    setSelectedTemperament(selectedValue);
    onChange(selectedValue);
  };

  return (
    <div>
      <div className={style.temperament}>
        <button
          className={style.select}
          onClick={() => setShowTemperaments(!showTemperaments)}
        >
          Show Temperaments
        </button>
        {showTemperaments && (
          <div>
            <form>
              {allTemperaments.map((temperament) => (
                <div key={temperament.id}>
                  <input
                    type="radio"
                    id={temperament.name}
                    name="temperament"
                    value={temperament.name}
                    checked={selectedTemperament === temperament.name}
                    onChange={handleTemperamentSelect}
                  />
                  <label htmlFor={temperament.name}>{temperament.name}</label>
                </div>
              ))}
            </form>
            <button onClick={() => onChange(selectedTemperament)}>Apply</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FilterBy;
