// import React from "react";
// import { useEffect, useState } from "react";
// import { useDispatch, useSelector } from "react-redux";
// import { getByTemperaments, filterByTemperaments } from "../../redux/actions";


const FilterBy = () => {
    const dispatch = useDispatch();
    const [filterValue, setFilterValue] = useState("");
    // const temperaments = useSelector((state) => state.temperaments);
    const filteredTemperaments = useSelector((state) => state.filteredTemperaments);


    useEffect(() => {
        // Aquí puedes cargar los temperamentos del backend o de alguna API externa
        // utilizando una función de Redux, por ejemplo, `fetchTemperaments`.
        // Aquí utilizo un array de ejemplo.
        const temperaments = [
          { name: "Friendly" },
          { name: "Energetic" },
          { name: "Calm" },
          { name: "Shy" },
          { name: "Confident" },
        ];
        dispatch(getByTemperaments(temperaments));
  }, [dispatch]);

  const handleFilter = (e) => {
    setFilterValue(e.target.value);
    dispatch(filterByTemperaments(e.target.value));
  };

  return (
    <div>
      <input type="text" value={filterValue} onChange={handleFilter} />
      <ul>
        {filteredTemperaments.map((temperament) => (
          <li key={temperament.name}>{temperament.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default FilterBy;
