import CardsContainer from "../../components/CardsContainer/CardsContainer"
import { useEffect, useState } from "react"; 
import { useDispatch, useSelector } from "react-redux"; 
import { getDogs } from "../../redux/actions";
import NavBar from "../../components/NavBar/NavBar" 



function  Home (){  
   //cuando se monta, que haga dispatch 
 const dispatch = useDispatch(); 
   const  allDogs = useSelector((state)=>state.allDogs);
   const  [filtered, setFiltered] = useState(allDogs); //f. Barra de busqueda, va a ser generica. componente reutilizable. estado local 
   const [searchString, setSearchString] = useState (""); 

const handleChange = (e)=> {
    e.preventDefault()   //funcion que setea 
    searchString(e.target.value)

}; 
const handleSubmit= ()=> {
    const filtered = allDogs.filter((dog)=>
    dog.name.includes(setSearchString)
    ); 
    setFiltered(filtered)
}

   useEffect(()=>{
    dispatch(getDogs()) 
   }, [dispatch])

    return (
    <>
    <h1>esta es la vista de Home</h1>
    <NavBar handleChange = {handleChange} handleSubmit={handleSubmit}/>
    <CardsContainer allDogs = {filtered}/> 

    </>

    )
}


export default Home
