import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import validate from "./validate";
import style from "./Form.module.css";
import { getTemperaments } from "../../redux/actions";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //useSelector

const Form = () => {
  //formulario y estado deben de tener los mismos valores
  const dispatch = useDispatch(); // AquÃ­ guardamos el hook useDispatch en una constante
  const tempForm = useSelector((state) => state.temperament);

  const [create, setCreate] = useState(false);

  const [form] = useState({
    name: "",
    min_height: "",
    max_height: "",
    min_weight: "",
    max_weight: "",
    life_spanmin: "",
    life_spanmax: "",
    temperaments: [],
    image: "",
  });
  const [completed, setCompleted] = useState(form);
  const [errors, setErrors] = useState({
    name: "",
    min_height: "",
    max_height: "",
    min_weight: "",
    max_weight: "",
    weight: "",
    life_spanmin: "",
    life_spanmax: "",
    temperaments: [],
    image: "",
  });

  const finalForm = {
    name: completed.name,
    height: `${completed.min_height} - ${completed.max_height}`,
    weight: `${completed.min_weight} - ${completed.max_weight}`,
    life_span: `${completed.life_spanmin} - ${completed.life_spanmax} years`,


    image: completed.image,
  };


  useEffect(() => {
    if (tempForm.length === 0) dispatch(getTemperaments());
  }, [tempForm.length, dispatch]);

  const changeHandler = (e) => {
    setCompleted({ ...completed, [e.target.name]: e.target.value });
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,
      })
    );
  };

  const handleTemperaments = (e) => {
    if (
      !completed.temperaments.includes(
        tempForm.find((item) => item.name === e.target.value)
      )
    ) {
      completed.temperaments.push(
        tempForm.find((item) => item.name === e.target.value)
      );
    }
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,
      })
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,
      })
    );

    axios.post("http://localhost:3001/dogs", { ...finalForm });
    setCreate(!create);
    setCompleted(form);
  };


  const handleDelete = (name) => {
  setCompleted({
    ...completed,
    temperaments: completed.temperaments.filter(
      (item) => item.name !== name
    ),
  });
};



  return (
    <div>
      <h2 className={style.title}>Create a new Dog</h2>

      <div className={style.cardContainer}>
        <form className={style.ent} onSubmit={handleSubmit}>
          <div>
            <label className={style.label}>Name:</label>
            <input
              type="text "
              value={completed.name}
              onChange={changeHandler}
              name="name"
              placeholder="Name"


        />
        {errors.name && <span>{errors.name}</span>}
      </div>

      <div>
        <label className={style.label} >Min Height:</label>
        <input
          type="text"
          value={completed.min_height}
          onChange={changeHandler}
          name="min_height"

          className="number"
              placeholder="Height Min"

        />
        {errors.min_height && <span>{errors.min_height}</span>}
      </div>

      <div>
        <label className={style.label} >Max Height:</label>
        <input
          type="text"
          value={completed.max_height}
          onChange={changeHandler}
          name="max_height"
          placeholder="Height Max"
        />
        {errors.max_height && <span>{errors.max_height}</span>}
      </div>
      <div></div>
      <div>
        <label className={style.label}>Min Weight:</label>
        <input
          type="text"
          value={completed.min_weight}
          onChange={changeHandler}
          name="min_weight"
          placeholder="Weight Min"
        />
         {errors.min_weight && <span>{errors.min_weight}</span>}
      </div>
      <div>
        <label className={style.label}>Max Weight:</label>
        <input
          type="text"
          value={completed.max_weight}
          onChange={changeHandler}
          name="max_weight"
          placeholder="Weight Max"
        />
        {errors.max_weight && <span>{errors.max_weight}</span>}
      </div>
      <div>
        <label className={style.label}>Life Span:</label>
        <input
          type="text "
          value={completed.life_span}
          onChange={changeHandler}
          name="life_span"
          placeholder="Life Span"
        />
        {errors.life_span && <span>{errors.life_span}</span>}
      </div>

      <label className={style.label}>Temperaments:</label>
        <select name="temperaments" onChange={handleTemperaments}>
          <option value="default">Choose</option>
          {tempForm?.map((item) => (
            <option value={item.name} key={item.id}>
              {item.name}
            </option>
          ))}
        </select>{" "}
        {errors.temperaments ? (
          <label>{errors.temperaments}</label>
        ) : null}
        <buton create={create}>
          {!create ? "SUBMIT" : "Creating..."}
        </buton>

        <div>
        <label className={style.label}>image:</label>
        <input
          type="text "
          value={form.image}
          onChange={changeHandler}
          name="image"
          placeholder="Put a URL"
        />
      </div>
      <button type="submit">Lets Create</button>
    </form>
    <div>
            {completed.temperament.map((item) => (
  <div className={style.temperament} key={item.name}>
    {item.name}
    <button onClick={() => handleDelete(item.name)}>x</button>
  </div>
))}

          </div>
    </div>
    <Link to="/home">
    <button className={style.btn}>Back To Home</button>
    </Link>
    </div>
  );
};

