import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByDetail } from "../../redux/actions";



const Detail = ({ match }) => {
    const { id } = match.params; // Obtenemos el ID de la URL

    const dispatch = useDispatch();
    const dogDetail = useSelector((state) => state.dogDetail);

    useEffect(() => {
      dispatch(getByDetail(id)); // Hacemos la llamada a la API para obtener la información del perro específico
    }, [dispatch, id]);

    if (!dogDetail) { // Si no hay información del perro aún, mostramos un mensaje de carga
      return <p>Cargando...</p>;
    }

    const { image = { url: "" }, name, weight, height, life_span, temperament } =
      dogDetail;

    return (
      <div>
        <h2>{name}</h2>
        <img src={image.url} alt={name} />
        <p>Peso: {weight.metric} kg</p>
        <p>Altura: {height.metric} cm</p>
        <p>Esperanza de vida: {life_span}</p>
        <p>Temperamento: {temperament}</p>
      </div>
    );
  };

  export default Detail;
