import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByDetail } from "../../redux/actions";

function Detail() {
    const dispatch = useDispatch();
    const detailDog = useSelector((state) => state.detailDog);

    const { id } = useParams(); // Obtenemos el valor del parámetro "id"

    useEffect(() => {
      dispatch(getByDetail(id)); // Pasamos el id al action creator
    }, [dispatch, id]);

  return (
    <>
      {dog ? (
        <div>
          <h1>{dog.name}</h1>
          <img src={dog.image.url} alt={dog.name} />
          <p>Weight: {dog.weight.metric} KG</p>
          <p>Height: {dog.height.metric} cm</p>
          <p>Life Span: {dog.life_span}</p>
          <p>Temperament: {dog.temperament}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </>
  );
};

export default Detail;
