import CardsContainer from "../../components/CardsContainer/CardsContainer"
import { useEffect, useState } from "react"; 
import { useDispatch, useSelector } from "react-redux"; 
import { getDogs } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar"; 
import FilterBy from "../../components/FilterBy/FilterBy";
import { getByName,getByTemperament } from "../../redux/actions";
import Pagination from "../../components/Pagination/Pagination";




function  Home (){  
    //cuando se monta, que haga dispatch 
  const dispatch = useDispatch(); 
    const  allDogs = useSelector((state)=>state.allDogs);
    const  [filtered, setFiltered] = useState(allDogs); //f. Barra de busqueda, va a ser generica. componente reutilizable. estado local 
    const [searchString, setSearchString] = useState (""); 
    const [selectedTemperaments, setSelectedTemperaments] = useState([]);
    ///pagination
    const [cardsPerPage]= useState(8);
    const [currentPage, setCurrentPage]=useState(1); 
    const maximo = Math.ceil(allDogs.length / cardsPerPage);
    const startIndex = (currentPage - 1) * cardsPerPage;
    const endIndex = startIndex + cardsPerPage;
    const currentCards = filtered.slice(startIndex, endIndex);



useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  useEffect(() => {
    setFiltered(allDogs);
  }, [allDogs]);

  const handleChange = (e) => {    //funcion que setea 
    setSearchString(e.target.value);
    const filteredDogs = allDogs.filter((dog) => {
      return dog.name.toLowerCase().includes(e.target.value.toLowerCase());
    });
    setFiltered(filteredDogs);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(getByName(searchString));
  };


  const handleSelect = (temperament) => {
    if (!selectedTemperaments.includes(temperament)) {
      setSelectedTemperaments([...selectedTemperaments, temperament]);
      dispatch(getByTemperament(temperament));
    }
  };

  const handleClearTemperaments = () => {
    setSelectedTemperaments([]);
    setFiltered(allDogs);
  };

  useEffect(() => {
    if (selectedTemperaments.length > 0) {
      const filteredDogs = allDogs.filter((dog) => {
        return dog.temperament?.some((temperament) =>
          selectedTemperaments.includes(temperament.name)
        );
      });
      setFiltered(filteredDogs);
    } else {
      setFiltered(allDogs);
    }
  }, [selectedTemperaments, allDogs]);






    return (
    <>


    <SearchBar handleChange = {handleChange} handleSubmit={handleSubmit}
    />


    <FilterBy/>
    <Pagination currentPage={currentPage} setCurrentPage={setCurrentPage} maximo={maximo}/>

    <CardsContainer allDogs = {currentCards}/>

      <Pagination currentPage={currentPage} setCurrentPage={setCurrentPage} maximo={maximo}/>


    </>

    )
}


export default Home
