import React, { useState, useEffect } from 'react';

const FilterBy = ({ allDogs}) => {
  const [selectedTemperament, setSelectedTemperament] = useState('');
  const [filteredDogs, setFilteredDogs] = useState([]);
  const temperaments = useSelector(state => state.temperaments);
  useEffect(() => {
    if (selectedTemperament) {
      const filtered = allDogs.filter(dog => dog.temperament.includes(selectedTemperament));
      setFilteredDogs(filtered);
    } else {
      setFilteredDogs([]);
    }
  }, [allDogs, selectedTemperament]);

  const handleSelectChange = e => {
    setSelectedTemperament(e.target.value);
  };

  return (
    <div>
      <h1>Dogs by Temperament</h1>
      <select value={selectedTemperament} onChange={handleSelectChange}>
        <option value="">-- Select a temperament --</option>
        {temperaments.map(temp => (
          <option key={temp.id} value={temp.name}>
            {temp.name}
          </option>
        ))}
      </select>
      <ul>
        {filteredDogs.map(dog => (
          <li key={dog.id}>
            <img src={dog.image} alt={dog.name} />
            <p>{dog.name}</p>
            <p>{dog.temperament}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FilterBy;
