import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByDetail } from "../../redux/actions";
import style from "./Detail.module.css";
import { useParams } from "react-router-dom";

function Detail() {
    const dispatch = useDispatch();
    const detailDog = useSelector((state) => state.detailDog);

    const { id } = useParams(); // Obtenemos el valor del parámetro "id"

    useEffect(() => {
      dispatch(getByDetail(id)); // Pasamos el id al action creator
    }, [dispatch, id]);


    return (
        <div className={style.detail}>
          <h1>{name}</h1>

          <div className={style.info}>
            <p>
              <span>Weight:</span> {weight.metric} KG
            </p>
            <p>
              <span>Height:</span> {height?.metric} cm
            </p>
            <p>
              <span>Life Span:</span> {life_span}
            </p>
            <p>
              <span>Temperament:</span> {temperament}
            </p>
          </div>
        </div>
      );
    }

    export default Detail;
