import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByDetail } from "../../redux/actions";
import style from "./Detail.module.css";
import { useParams } from "react-router-dom";

const Detail = ({ match }) => {
  const dispatch = useDispatch();
  const dogDetail = useSelector(state => state.dogDetail);

  useEffect(() => {
    dispatch(getByDetail(match.params.id));
  }, [dispatch, match.params.id]);

  if (!dogDetail) return <h2>Loading...</h2>;

  const { name, image, weight, height, life_span, temperament } = dogDetail;

  return (
    <div className={style.container}>
      <div className={style.card}>
        <div className={style.front}>
          <img src={image.url} alt={name} />
        </div>
        <div className={style.back}>
          <h2 className={style.name}>{name}</h2>
          <p>Weight: {weight.metric} KG</p>
          <p>Height: {height.metric} CM</p>
          <p>Life Span: {life_span}</p>
          <p>Temperament: {temperament}</p>
        </div>
      </div>
    </div>
  );
};

export default Detail;
