import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import validate from "./validate";
import style  from "./Form.module.css"
import { getTemperaments } from "../../redux/actions";
import { useEffect } from "react";
import {  useDispatch, useSelector } from "react-redux"; //useSelector
const Form = () => {
  //formulario y estado deben de tener los mismos valores
  const dispatch = useDispatch(); // AquÃ­ guardamos el hook useDispatch en una constante
  const tempForm = useSelector((state) => state.temperament);
  const [selectedTemperaments, setSelectedTemperaments] = useState([]);


  const [form, setForm] = useState({
    name: "",
    min_height: "",
    max_height: "",
    min_weight: "",
    max_weight: "",
    life_spanmin: "",
    life_spanmax: "",
    temperaments: [],
    image: "",
  });
  const [completed, setCompleted] = useState(form);
  const [errors, setErrors] = useState({
    name: "",
    min_height: "",
    max_height: "",
    min_weight: "",
    max_weight: "",
    weight: "",
    life_spanmin: "",
    life_spanmax: "",
    temperaments: [],
    image: "",
  });

  const finalForm = {
    name: completed.name,
    height: `${completed.min_height} - ${completed.max_height}`,
    weight: `${completed.min_weight} - ${completed.max_weight}`,
    life_span: `${completed.life_spanmin} - ${completed.life_spanmax} years`,
    temperaments: completed.temperament.map((item) => item.id),
    image: completed.image,
  };


  useEffect(() => {
    if (tempForm.length === 0) 
    dispatch(getTemperaments());
  }, [tempForm.length, dispatch]);


  const changeHandler = (e) => {
    setCompleted({ ...completed, [e.target.name]: e.target.value });
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,
      })
    );
  };
  const handleTemperaments = (e) => {
    if (!completed.temperaments.includes(tempForm.find( item => item.name === e.target.value))) {
        completed.temperaments.push(tempForm.find( item => item.name === e.target.value))
       }
    // completed.temperaments.push(tempForm.find( item => item.name === e.target.value))
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,
      })
    );
  };

  // const changeHandler = (e) => {
  //   //e, me dice quien disparo el event
  //   const property = e.target.name;
  //   const value = e.target.value;

  //   setErrors(validate({ ...form, [property]: value })); //el mismo estado tiene validate y va a la par
  //   setForm({ ...form, [property]: value }); //cambio el estado y cambia el input
  // };

  // const submitHandler = (e) => {
  //   const height = `${form.min_height}-${form.max_height}`; //creo las variables y las concateno separandolas con un guion
  //   const weight = `${form.min_weight}-${form.max_weight}`;


  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors(
      validate({
        ...completed,
        [e.target.name]: e.target.value,

      })
    );


    e.preventDefault(); ///ya no se va la pag ref
    axios
      .post("http://localhost:3001/dogs", { ...form, }) //envio el body form  junto con las variables (weight y hei)
      .then((res) => alert(res))
      .catch((err) => alert(err));
  };

  return (
    <div>
        <h2 className={style.title}>Create a new Dog</h2>


    <div className= {style.cardContainer}>


    <form className= { style.ent} onSubmit={submitHandler}>
      <div>


        <label className={style.label}>Name:</label>
        <input
          type="text "
          value={form.name}
          onChange={changeHandler}
          name="name"
          placeholder="Name"

        />
        {errors.name && <span>{errors.name}</span>}
      </div>

      <div>
        <label className={style.label} >Min Height:</label>
        <input
          type="text"
          value={form.min_height}
          onChange={changeHandler}
          name="min_height"

          className="number"
              placeholder="Height Min"

        />
