import { GET_DOGS, GET_BY_NAME, GET_BY_DETAIL, GET_TEMPERAMENTS,FILTER_BY,GET_BY_TEMPERAMENTS,CHARGE_ALL,GET_BY_GROUP, ORDER_BY} from "./actions"

const initialState ={    //estado global al inicio de mi aplicacion 
allDogs:[],dogsCopy:[], detailDog: {}, temperaments:[], filter:[], breed_group:[] //copia del estado para que sea exactamente igual a allDogs|| es una manera de regresar al estado origina; 

}; 


export const rootReducer=(state = initialState,  action)=>{ //es una funcion que es quien sabe que va a modificar 
  switch (action.type) {
    case GET_DOGS:
        return {...state, ///retorna una copia del estado, modificando el valor que quiero modificar y retorna un estado nuevo 
            allDogs: action.payload, 
            dogsCopy: action.payload
        };   
      case GET_BY_NAME:
        return {...state, 
          allDogs:action.payload,

        }; 

     case GET_BY_DETAIL:
         return{...state,
          detailDog:action.payload,
      }; 


    case GET_TEMPERAMENTS:
        return {...state,
        temperaments:action.payload,

      }; 

   case GET_BY_TEMPERAMENTS:
     return {...state, 
      filter: state.dogs.filter(el=>el.temperaments? el.temperaments.includes(action.payload):null)

    }; 

    case FILTER_BY:
      if(action.payload === 'DB'){
        if( state.dogs.filter((item)=> (typeof item.id) === 'string').length === 0 ){
           return alert('no dog')
        }
        return {
            ...state, 
            filter: state.dogs?.filter((item)=> (typeof item.id) === 'string')}
        }

    else {
        return {
            ...state, 
            filter: state.dogs.filter((item)=> (typeof item.id) === 'number')}
        } ;  


    case CHARGE_ALL:
      return{
        ...state,
        filter:state.allDogs
      }; 

    case GET_BY_GROUP:
    return{
      ...state,
      filter: state.allDogs.filter(el=>el.breed_group? el.breed_group.includes(action.payload):null)
    }; 

    case ORDER_BY:


    default:
        return state; 
  }
}

export default rootReducer;
