import { useState } from "react"; 
import axios from "axios"; 
// import { Link } from "react-router-dom"; 

const Form = () => { //formulario y estado deben de tener los mismos valores

    const [form,setForm] = useState({
        name:"",
        min_height: "",
        max_height: "",
        weight:"",
        life_span:"",
        temperament:"", 
        image:"",

    }) 

    const [errors, setErrors] = useState({
        name:"",
        min_height: "",
        max_height: "",
        weight:"",
        life_span: "",
        temperament: "", 
        image:"",


    })
    const changeHandler = (e) =>{ //e, me dice quien disparo el event 
         const property = e.target.name; 
         const value = e.target.value; 


         validate({...form, [property]:value}) //el mismo estado tiene validate y va a la par


         setForm({...form, [property]:value}) //cambio el estado y cambia el input


    }
    const validate = (form) =>{
       if( /^[A-Za-z]+/.test(form.name)){
        setErrors({...errors, name:""})

       }else{
        setErrors({...errors, name:"Name error"})
       }
       if(form.name==="") setErrors({...errors, name: "this field can not be blank"})


    }
    const submitHandler= (e)=>{
        const height = `${form.min_height}-${form.max_height}`;
        e.preventDefault() ///ya no se va la pag ref
        axios.post("http://localhost:3001/dogs", form)//envio el body (,form)
        .then(res=>alert(res))
        .catch(err=>alert(err))


    }



    return (

    <form onSubmit={submitHandler}>
      <div>
        <label>Name:</label>
        <input type= "text " value={form.name} onChange={changeHandler} name= "name"/>
       {errors.name && <span>{errors.name}</span>}  
      </div>
      {/* <div>
        <label>Height:</label>
        <input type= "text" value={form.height} onChange={changeHandler} name= "height"/>
      </div> */}

      <div>
        <label>Minimum Height:</label>
        <input
          type="text"
          value={form.min_height}
          onChange={changeHandler}
          name="min_height"
        />
      </div>

      <div>
        <label>Maximum Height:</label>
        <input
          type="text"
          value={form.max_height}
          onChange={changeHandler}
          name="max_height"
        />

      </div>

      <div>
        <label>Weight:</label>
        <input type= "text" value={form.weight} onChange={changeHandler} name= "weight"/>
      </div>
      <div>
        <label>Life Span:</label>
        <input type= "text " value={form.life_span}  onChange={changeHandler} name= "life_span"/>
      </div>
      <div>
        <label>Chose a Temperament:</label>
        <input type= "text " value={form.temperament} onChange={changeHandler} name= "temperament"/>
      </div>
      <div>
        <label>image:</label>
        <input type= "text " value={form.image} onChange={changeHandler} name= "image"/>
      </div>
      <button type="submit">Lets Create</button>
    </form>







  );
};

export default Form;
