import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTemperaments } from "../../redux/actions";

const FilterBy = () => {
  const dispatch = useDispatch();
  const temperaments = useSelector((state) => state.temperaments);
  const [showTemperaments, setShowTemperaments] = useState(false);




   const handleTemperamentSelect = (event) => {
    setSelectedTemperament(event.target.value);
  };

  return (
    <div>
        <div className={style.temperament}>
      <button className={style.select}onClick={() => setShowTemperaments(!showTemperaments)}>Show Temperaments</button>
      {showTemperaments && (
          <div>
            <form>
              {temperaments.map((temperament) => (
                <div key={temperament.id}>
                  <input
                    type="radio"
                    id={temperament.name}
                    name="temperament"
                    value={temperament.name}
                    checked={selectedTemperament === temperament.name}
                    onChange={handleTemperamentSelect}
                  />
                  <label htmlFor={temperament.name}>{temperament.name}</label>
                </div>
              ))}
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default FilterBy;
