  return (
    <div>
      <button onClick={toggleTemperaments}>Show Temperaments</button>
      {showTemperaments && (
        <div className="temperaments-container">
          <input type="text" value={filterValue} onChange={handleFilter} />
          <ul>
            {filteredTemperaments.map((temperament) => (
              <li key={temperament.name}>{temperament.name}</li>
            ))}
          </ul>
        </div>
      )}
      <style>{`
        .temperaments-container {
          position: absolute;
          top: 40px;
          left: 0;
          width: 100%;
          background-color: #fff;
          box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
          z-index: 1;
        }
        button {
          background-color: #4CAF50;
          color: white;
          padding: 12px 24px;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          font-size: 16px;
          position: relative;
          transition: all 0.3s ease-in-out;
        }
        button:hover {
          background-color: #3e8e41;
        }
        button:active {
          top: 2px;
        }
      `}</style>
    </div>
  );
};

export default FilterBy;
