import { useDispatch, useSelector } from "react-redux";
import { getTemperaments, filterByTemperament } from "../../redux/actions";
import React, { useEffect } from "react";


const FilterBy=()=>{
    const dispatch = useDispatch();
    const temperaments = useSelector((state) => state.temperaments);

    useEffect(() => {
      dispatch(getTemperaments());
    }, [dispatch]);

    const handleTemperamentFilter = (event) => {
      const selectedTemperament = event.target.value;
      dispatch(filterByTemperament(selectedTemperament));
    };
    return (
        <div>
          <label>Filter by temperament:</label>
          <select onChange={handleTemperamentFilter}>
            <option value="">All</option>
            {temperaments && temperaments.length > 0 && temperaments.map((temperament) => (
  <option key={temperament.id} value={temperament.name}>
    {temperament.name}
              </option>
            ))}
          </select>
        </div>
      );


}
export default FilterBy;
