import React, { useState } from "react";
import style from "./FilterBy.module.css"; 
import { getByTemperaments, filterBy, chargeAll, orderBy } from "../../redux/actions";
import { useDispatch, useSelector } from "react-redux";

const FilterBy = () => {
  const dispatch = useDispatch();
  const temperaments = useSelector((state) => state.temperaments);
  const [selectedTemperaments, setSelectedTemperaments] = useState([]);

  const handleSelect = (e) => {
    const selected = e.target.value;
    if (selected !== "default" && !selectedTemperaments.includes(selected)) {
      const updatedSelectedTemperaments = [...selectedTemperaments, selected];
      setSelectedTemperaments(updatedSelectedTemperaments);
      dispatch(getByTemperaments(updatedSelectedTemperaments));
    }
    e.target.value = "default";
  };

  const handleSelect2 = (e) => {
    dispatch(orderBy(e.target.value));
    e.target.value = "default";
  };

  const handleRemoveTemperament = (temperament) => {
    const updatedSelectedTemperaments = selectedTemperaments.filter((t) => t !== temperament);
    setSelectedTemperaments(updatedSelectedTemperaments);
    dispatch(getByTemperaments(updatedSelectedTemperaments));
  };

  return (
    <section>
      <div className={style.selectdiv}>
        <select className={style.select} onChange={handleSelect} name="" id="">
          <option value="default">FILTER BY...</option>
          <optgroup label="TEMPERAMENTS...">
            {temperaments &&
              temperaments.map((el) => (
                <option key={el.name} value={el.name}>
                  {el.name}
                </option>
              ))}
          </optgroup>
        </select>

        <select className={style.select} onChange={handleSelect2} name="" id="">
          <option value="default">ORDER BY...</option>
          <optgroup label="Weight">
            <option value="asc">Max to Min</option>
            <option value="desc">Min to Max</option>
          </optgroup>
          <optgroup label="Alphabetic">
            <option value="A-Z">A - Z</option>
            <option value="Z-A">Z - A</option>
          </optgroup>
        </select>

        <div className={style.selectedTemperaments}>
          {selectedTemperaments.length > 0 &&
            selectedTemperaments.map((temperament) => (
              <div className={style.selectedTemperament} key={temperament}>
                <span>{temperament}</span>
                <button onClick={() => handleRemoveTemperament(temperament)}>x</button>
              </div>
            ))}
        </div>

        <div className={style.buttons}>
          <button className={style.buton} value="TODOS" onClick={() => dispatch(chargeAll())}>
            ALL
          </button>
          <button className={style.buton} value="DB" onClick={() => dispatch(filterBy("DB"))}>
            DATABASE
          </button>
          <button className={style.buton} value="API" onClick={() => dispatch(filterBy("API"))}>
            API
          </button>
        </div>
      </div>
    </section>
  );
};

export default FilterBy;
