import React, { useState } from "react";

const FilterBy = ({ items, onFilter, onSort }) => {
  const [selectedTemperament, setSelectedTemperament] = useState("");
  const [selectedSort, setSelectedSort] = useState("");

  const handleTemperamentChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedTemperament(selectedValue);
    onFilter(selectedValue);
  };

  const handleSortChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedSort(selectedValue);
    onSort(selectedValue);
  };

  return (
    <div>
      <label htmlFor="temperament-select">Filter by Temperament:</label>
      <select
        id="temperament-select"
        value={selectedTemperament}
        onChange={handleTemperamentChange}
      >
        <option value="">All</option>
        {items.map((item) => (
          <option key={item.temperament} value={item.temperament}>
            {item.temperament}
          </option>
        ))}
      </select>
      <label htmlFor="sort-select">Sort by:</label>
      <select
        id="sort-select"
        value={selectedSort}
        onChange={handleSortChange}
      >
        <option value="">Default</option>
        <option value="name-asc">Name (Ascending)</option>
        <option value="name-desc">Name (Descending)</option>
        <option value="weight-asc">Weight (Ascending)</option>
        <option value="weight-desc">Weight (Descending)</option>
      </select>
    </div>
  );
};

export default FilterBy;
