import { GET_DOGS } from "./actions"

const initialState ={    //estado global al inicio de mi aplicacion 
    dogs: {allDogs:[]},
}; 


const rootReducer=(state = initialState,  action)=>{ //es una funcion que es quien sabe que va a modificar 
  switch (action.type) {
    case GET_DOGS:
        return {...state, dogs: action.payload}   ///retorna una copia del estado, modificando el valor que quiero modificar y retorna un estado nuevo 

    default:
        return {...state}
  }
}

export default rootReducer;
