import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getByDetail } from "../../redux/actions";
import style from "./Detail.module.css";
function Detail() {
    const dispatch = useDispatch();
    const detailDog = useSelector((state) => state.detailDog);

    const { id } = useParams(); // Obtenemos el valor del parámetro "id"

    useEffect(() => {
      dispatch(getByDetail(id)); // Pasamos el id al action creator
    }, [dispatch, id]);

    return (
        <div className={style.detail}>
          <h1>{detailDog.name}</h1>
          <img src={detailDog.image?.url} alt={detailDog.name} />
          <div className={style.info}>
            <p>
              <span>Weight:</span> {detailDog.weight?.metric} KG
            </p>
            <p>
              <span>Height:</span> {detailDog.height?.metric} cm
            </p>
            <p>
              <span>Life Span:</span> {detailDog.life_span}
            </p>
            <p>
              <span>Temperament:</span> {detailDog.temperament}
            </p>
          </div>
        </div>
      );
    }

    export default Detail;
