import CardsContainer from "../../components/CardsContainer/CardsContainer"
import { useEffect, useState } from "react"; 
import { useDispatch, useSelector } from "react-redux"; 
import { getDogs } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar" 



function  Home (){  
   //cuando se monta, que haga dispatch 
 const dispatch = useDispatch(); 
   const  allDogs = useSelector((state)=>state.allDogs);
//    const  [filtered, setFiltered] = useState(allDogs); //f. Barra de busqueda, va a ser generica. componente reutilizable. estado local 
   const [searchString, setSearchString] = useState (""); 


//filtro con el Back end

const handleChange = (e)=> {
    e.preventDefault()   //funcion que setea 
    setSearchString(e.target.value); 
    if (e.target.value === "") { // Si la cadena de búsqueda está vacía
        setFiltered(allDogs); // Mostrar todos los perros
    }

}; 
const handleSubmit= (e)=> {
    e.preventDefault()
    const filtered = allDogs.filter((dog)=>
    dog.name.includes(searchString)
    ); 
    setFiltered(filtered)
}

   useEffect(()=>{
    dispatch(getDogs()) 
   }, [dispatch]); 

   useEffect(()=>{
    setFiltered(allDogs); 

   }, [allDogs])

    return (
    <>

    <SearchBar handleChange = {handleChange} handleSubmit={handleSubmit}/>
    <CardsContainer allDogs = {filtered}/> 

    <h1>Estoy en Home</h1>

    </>

    )
}


export default Home
