import axios from 'axios'; 
export const GET_DOGS = "GET_DOGS";
export const GET_BY_NAME = "GET_BY_NAME" 

import CardsContainer from "../../components/CardsContainer/CardsContainer";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar";

function Home() {
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.allDogs);
  const [filtered, setFiltered] = useState(allDogs);
  const [searchString, setSearchString] = useState("");
  const [selectedDog, setSelectedDog] = useState(null);

  const handleChange = (e) => {
    e.preventDefault();
    setSearchString(e.target.value);
    if (e.target.value === "") {
      setFiltered(allDogs);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const filtered = allDogs.filter((dog) => dog.name.includes(searchString));
    setFiltered(filtered);
    clearDetail();
  };

  const clearDetail = () => {
    setSelectedDog(null);
  };

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  useEffect(() => {
    setFiltered(allDogs);
    clearDetail();
  }, [allDogs]);

  return (
    <>
      <SearchBar handleChange={handleChange} handleSubmit={handleSubmit} />
      <CardsContainer allDogs={filtered} setSelectedDog={setSelectedDog} />
      {selectedDog && <DogDetails selectedDog={selectedDog} />}
    </>
  );
}

export default Home;
