import CardsContainer from "../../components/CardsContainer/CardsContainer"
import { useEffect, useState } from "react"; 
import { useDispatch, useSelector } from "react-redux"; 
import { getDogs } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar"; 
import FilterBy from "../../components/FilterBy/FilterBy"; 
import { getByName } from "../../redux/actions";
import Pagination from "../../components/Pagination/Pagination";
function Home() { 





  const dispatch = useDispatch(); 
  const allDogs = useSelector((state) => state.allDogs);
  const [filtered, setFiltered] = useState(allDogs);
  const [searchString, setSearchString] = useState("");
  const [cardsPerPage] = useState(8);
  const [currentPage, setCurrentPage] = useState(1);
  const indexOfLastCard = currentPage * cardsPerPage;
  const indexOfFirstCard = indexOfLastCard - cardsPerPage;
  const currentCards = filtered.slice(indexOfFirstCard, indexOfLastCard);
  const totalCards = filtered.length;
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(totalCards / cardsPerPage); i++) {
    pageNumbers.push(i);
  }

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  useEffect(() => {
    setFiltered(allDogs);
  }, [allDogs]);

  const handleChange = (e) => {   
    setSearchString(e.target.value);
    const filteredDogs = allDogs.filter((dog) => {
      return dog.name.toLowerCase().includes(e.target.value.toLowerCase());
    });
    setFiltered(filteredDogs);
    setCurrentPage(1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(getByName(searchString));
    setCurrentPage(1);
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <>
      <SearchBar handleChange={handleChange} handleSubmit={handleSubmit} />
      <FilterBy />
      <CardsContainer allDogs={currentCards} />
      <Pagination
        itemsPerPage={cardsPerPage}
        totalItems={totalCards}
        paginate={paginate}
        currentPage={currentPage}
        pageNumbers={pageNumbers}
      />
    </>
  );
}

export default Home;
