import axios from 'axios'; 
export const GET_DOGS = "GET_DOGS"; 

const getDogs =()=> {
    return async function(dispatch){ //esta funcion hace la request  y cuando tiene la info hace un dispatch 
      const apiData =await axios.get ('https://api.thedogapi.com/v1/breeds' 
      ); 
      const dogs = apiData.data; //info 
         dispatch ({ type: GET_DOGS, payload: dogs}); //const GET_DOGS . el reducer recibe esta action 
    }
}
