import CardsContainer from "../../components/CardsContainer/CardsContainer"
import { useEffect, useState } from "react"; 
import { useDispatch, useSelector } from "react-redux"; 
import { getDogs } from "../../redux/actions";
import SearchBar from "../../components/NavBar/NavBar"; 
import F from "../../components/NavBar/NavBar"; 
import { getByName } from "../../redux/actions";



function  Home (){  
    //cuando se monta, que haga dispatch 
  const dispatch = useDispatch(); 
    const  allDogs = useSelector((state)=>state.allDogs);
    const  [filtered, setFiltered] = useState(allDogs); //f. Barra de busqueda, va a ser generica. componente reutilizable. estado local 
    const [searchString, setSearchString] = useState (""); 

//  const handleChange = (e)=> {
//      e.preventDefault()   //funcion que setea 
//      setSearchString(e.target.value); 
//      if (e.target.value === "") { // Si la cadena de búsqueda está vacía
//          setFiltered(allDogs); // Mostrar todos los perros
//      }

//  }; ///arreglar mañana. solo me busca los de la base de datos 

//  const handleSubmit=(e)=>{
//      e.preventDefault()
//      dispatch(getByName(searchString))
//  }


//     useEffect(()=>{
//      dispatch(getDogs()) 
//     }, [dispatch]); 

//     useEffect(()=>{
//      setFiltered(allDogs); 

//     }, [allDogs])
useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  useEffect(() => {
    setFiltered(allDogs);
  }, [allDogs]);

  const handleChange = (e) => {    //funcion que setea 
    setSearchString(e.target.value);
    const filteredDogs = allDogs.filter((dog) => {
      return dog.name.toLowerCase().includes(e.target.value.toLowerCase());
    });
    setFiltered(filteredDogs);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(getByName(searchString));
  };
    return (
    <>

    <SearchBar handleChange = {handleChange} handleSubmit={handleSubmit}/>
    <CardsContainer allDogs = {filtered}/>


    <h1>Estoy en Home</h1>

    </>

    )
}


export default Home
